pragma solidity ^0.4.21;
import './SimpleToken.sol';

contract Subscription {
	SimpleToken public token;
	address public subscriber;
	address public provider;
	uint public paymentPeriod; // In seconds how long the provider has to wait withdraw
	uint public amountPerPeriod; // Amount in wei how many tokens can be transferred per period.

	function Subscription (
		address tokenAddress, 
		address subscriberAddress,
		address providerAddress, 
		uint initialPaymentPeriod) {
		token = SimpleToken(tokenAddress);
		subscriber = subscriberAddress;
		provider = providerAddress;
		paymentPeriod = initialPaymentPeriod;
	}

	function collectPayment() external {
		token.transferFrom(subscriber, provider, amountPerPeriod);
	}
}
